select material_name.material_name, param_name,param_intro,param_value,test_temp,test_stress,std_num,param_maintainer,alloy_report_info.report_name from  alloy_param_data,alloy_param,material_name,alloy_standard_info,alloy_report_info where alloy_param_data.param_id=alloy_param.id and alloy_param_data.material_name =cast(material_name.material_id as character varying) and alloy_param_data.standard_id =alloy_standard_info.id and alloy_param_data.report_id =alloy_report_info.id and param_id=29 order by test_temp;